mapper.select:
select
  app_id, execute_id, app_key, app_ip, app_name, app_type
from app_info


        and app_id = #{appId}


        and execute_id = #{executeId}


        and app_key = #{appKey}


        and app_ip = #{appIp}


        and app_name = #{appName}


        and app_type = #{appType}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from app_info


        and app_id = #{appId}


        and execute_id = #{executeId}


        and app_key = #{appKey}


        and app_ip = #{appIp}


        and app_name = #{appName}


        and app_type = #{appType}
