mapper.select:
select
  template_id, template_name, template_type, request_type, json_template, url_template, sort_no, remark
from http_template


        and template_id = #{templateId}


        and template_name = #{templateName}


        and template_type = #{templateType}


        and request_type = #{requestType}


        and json_template = #{jsonTemplate}


        and url_template = #{urlTemplate}


        and sort_no = #{sortNo}


        and remark = #{remark}



    limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from http_template


        and template_id = #{templateId}


        and template_name = #{templateName}


        and template_type = #{templateType}


        and request_type = #{requestType}


        and json_template = #{jsonTemplate}


        and url_template = #{urlTemplate}


        and sort_no = #{sortNo}


        and remark = #{remark}
